const headerEl=document.querySelector(".navigation");window.addEventListener("scroll",(()=>{window.scrollY>100?headerEl.classList.add("header-scrolled"):window.scrollY<=100&&headerEl.classList.remove("header-scrolled")}));const accordionItems=document.querySelectorAll(".accordion-item");accordionItems.forEach((e=>e.addEventListener("click",(()=>{const o=e.classList.contains("open");accordionItems.forEach((e=>e.classList.remove("open"))),o||e.classList.toggle("open")}))));